import React, { useState } from 'react'
import "./styles.css"
import Menu from "./menuApi"
import MenuCard from "./MenuCard"

const Resturant = () => {

    //use state Hook/or prefered redux/  (on rules we have to apply hooks on top of the code i.e above return)
    const [menuData, setMenuData] = useState(Menu);

    const filterItem = (category) => {
        const upDatedList = Menu.filter((curElen) => {
            return curElen.category === category

        })
        setMenuData(upDatedList)

    };


    return (
        <>
        <nav className='navbar'>
            <div className='btn-group'>
                 <button className='btn-group__item'onClick={()=>fiterItem("Breakfast")}>Breakfast</button>
                 <button className='btn-group__item'>lunch</button>
                 <button className='btn-group__item'>Dinner</button>
                 <button className='btn-group__item'>non-veg</button>
                 <button className='btn-group__item'>all Menu</button>

            </div>

        </nav>

        
        
        
        < MenuCard menuData ={menuData}/>
    
        </>
    )
}

export default Resturant;
